import axios from 'axios';

const API_URL = 'http://localhost:8080/contact/messages'; // Adjust the URL if needed

class ContactService {
  // Get all messages
  getAllMessages() {
    return axios.get(API_URL)
      .then(response => response.data)  // Ensure the data is being returned properly
      .catch(error => {
        console.error('Error fetching messages:', error);
        throw error;  // Rethrow to handle it in the component
      });
  }

  // Get a single message by ID
  getMessageById(id) {
    return axios.get(`${API_URL}/${id}`); // Fetch a message by its ID
  }

  // Create a new message
  createMessage(message) {
    return axios.post(API_URL, message); // Post a new message
  }

  // Update an existing message
  updateMessage(id, updatedMessage) {
    return axios.put(`${API_URL}/${id}`, updatedMessage); // Update a message by ID
  }

  // Delete a message
  deleteMessage(id) {
    return axios.delete(`${API_URL}/${id}`); // Delete a message by ID
  }
}

export default new ContactService();
