import axios from "axios";
import AuthService from "@/services/AuthService";

const cart_url = "http://localhost:8080/cart";

class CartService {
  // Ensure userId is retrieved properly
  getCart() {
    const token = AuthService.getToken(); // ✅ Now correctly accessing `getToken`
    const userId = AuthService.getUserId();
    if (!userId) {
      console.error("User ID is missing");
      return Promise.reject("User ID is missing");
    }
    return axios.get(`${cart_url}?userId=${userId}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
  }

  // Add a product to the user's cart
  addToCart(userId, productId, quantity) {
    const token = AuthService.getToken();
    return axios.post(cart_url, null, {
      params: {
        userId: userId,
        productId: productId,
        quantity: quantity,
      },
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  // Remove a product from the user's cart
  removeFromCart(userId, productId) {
    const token = AuthService.getToken();
    return axios.delete(`${cart_url}/remove/${productId}`, {
      params: {
        userId: userId,
      },
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  // Update quantity of a product in the user's cart
  updateQuantity(userId, productId, quantity) {
    const token = AuthService.getToken();
    return axios.put(cart_url, null, {
      params: {
        userId: userId,
        productId: productId,
        quantity: quantity,
      },
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }
}

export default new CartService();
