import AuthService from "@/services/AuthService";
export default {
  data() {
    return {
      email: "",
      password: "",
      error: ""
    };
  },
  methods: {
    async login() {
      try {
        const response = await AuthService.login({
          email: this.email,
          password: this.password
        });
        this.$router.push("/products"); // Redirect to product list page
      } catch (error) {
        this.error = "Invalid email or password.";
      }
    }
  }
