import axios from "axios";

const product_url = "http://localhost:8080/products";

class ProductService {
  getProducts() {
    const token = AuthService.getToken(); // Assuming you have a method to get the token
    return axios.get(product_url, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  getProductById(id) {
    const token = AuthService.getToken();
    return axios.get(`${product_url}/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  createProduct(product) {
    const token = AuthService.getToken();
    return axios.post(product_url, product, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  updateProduct(product, id) {
    const token = AuthService.getToken();
    return axios.put(`${product_url}/${id}`, product, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }

  deleteProduct(id) {
    const token = AuthService.getToken();
    return axios.delete(`${product_url}/${id}`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
  }
}

export default new ProductService();
