import axios from "axios";
export default {
  name: "LoginPage",
  data() {
    return {
      email: "",
      password: "",
      error: "",
      success: ""
    };
  },
  methods: {
    async login() {
      // ðŸ§¹ Clear old session before login
      localStorage.removeItem("user");
      localStorage.removeItem("token");
      delete axios.defaults.headers.common["Authorization"];
      try {
        const response = await axios.post("http://localhost:8080/auth/login", {
          email: this.email,
          password: this.password
        });
        const token = response.data.token;
        const user = response.data.user || response.data; // adjust if user is nested

        if (token) {
          // âœ… Save session
          localStorage.setItem("token", token);
          localStorage.setItem("user", JSON.stringify(user));
          axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
          this.success = "Login successful!";
          this.error = "";

          // ðŸ” Redirect after short delay (or instantly if preferred)
          setTimeout(() => {
            this.redirectUser("UserDashboard"); // replace with actual route
          }, 1000);
        } else {
          this.error = "Login failed: token not received.";
          this.success = "";
        }
      } catch (err) {
        this.error = "Invalid credentials. Please try again.";
        this.success = "";
        console.error("Login failed:", err);
      }
    },
    redirectUser(routeName) {
      this.$router.replace({
        name: routeName
      });
    }
  }
};
