<template>
  <div class="container mt-5">
    <h2 class="text-center mb-4">Admin Profile</h2>
    <div v-if="!user" class="alert alert-info">
      <p>Loading...</p>
    </div>
    <div v-else>
      <p><strong>Name:</strong> {{ user.name }}</p>
      <p><strong>Email:</strong> {{ user.email }}</p>
      <p><strong>Role:</strong> {{ user.role }}</p>
      <button @click="showModal" class="btn btn-primary mt-3">
        Edit Profile
      </button>
      <button @click="goBack" class="btn btn-secondary mt-3 ml-2">
        Back to Dashboard
      </button>
    </div>

    <!-- Edit Profile Modal -->
    <div
      class="modal fade"
      id="editProfileModal"
      tabindex="-1"
      aria-labelledby="editProfileModalLabel"
      aria-hidden="true"
    >
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">
            <h5 class="modal-title" id="editProfileModalLabel">Edit Profile</h5>
            <button
              type="button"
              class="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
            ></button>
          </div>
          <div class="modal-body">
            <form @submit.prevent="updateProfile">
              <div class="mb-3">
                <label>Name:</label>
                <input
                  type="text"
                  class="form-control"
                  v-model="userForm.name"
                  required
                />
              </div>
              <div class="mb-3">
                <label>Email:</label>
                <input
                  type="email"
                  class="form-control"
                  v-model="userForm.email"
                  required
                />
              </div>
              <button type="submit" class="btn btn-success">Update Profile</button>
              <button type="button" class="btn btn-secondary ml-2" data-bs-dismiss="modal">Cancel</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import AuthService from "@/services/AuthService";
import { Modal } from "bootstrap";

export default {
  name: "AdminProfile",
  data() {
    return {
      user: null,
      userForm: {
        name: "",
        email: "",
      },
    };
  },
  async mounted() {
    this.fetchAdminProfile();
  },
  methods: {
    async fetchAdminProfile() {
      try {
        this.user = AuthService.getCurrentUser();
        this.userForm = { ...this.user };
      } catch (error) {
        console.error("Error fetching admin profile:", error);
      }
    },
    showModal() {
      const modal = new Modal(document.getElementById("editProfileModal"));
      modal.show();
    },
    async updateProfile() {
      try {
        // Implement the logic to update the profile
        console.log("Profile updated:", this.userForm);
        this.user = { ...this.userForm };
        const modal = Modal.getInstance(document.getElementById("editProfileModal"));
        modal.hide();
      } catch (error) {
        console.error("Error updating profile:", error);
      }
    },
    goBack() {
      this.$router.push({ name: "AdminDashboard" });
    },
  },
};
</script>

