import { createRouter, createWebHistory } from 'vue-router';
import Home from '@/components/HomePage.vue';
import Login from '@/components/LoginPage.vue';
import Register from '@/components/RegisterPage.vue';
// Admin pages
import AdminDashboard from '@/components/admin/AdminDashboard.vue';
import AdminProfile from '@/components/admin/AdminProfile.vue';
import AdminUsers from '@/components/admin/AdminUsers.vue';
import AdminProducts from '@/components/admin/AdminProducts.vue';
// User pages
import UserDashboard from '@/components/user/UserDashboard.vue';
import UserProfile from '@/components/user/UserProfile.vue';
import ProductList from '@/components/user/ProductList.vue';
import UserCart from '@/components/user/UserCart.vue';

const routes = [
  { path: '/', name: 'Home', component: Home },
  { path: '/login', name: 'Login', component: Login },
  { path: '/register', name: 'Register', component: Register },
  // Admin routes
  {
    path: '/admin/dashboard',
    name: 'AdminDashboard',
    component: AdminDashboard,
    meta: { requiresAuth: true, role: 'ROLE_ADMIN' },
  },
  {
    path: '/admin/profile',
    name: 'AdminProfile',
    component: AdminProfile,
    meta: { requiresAuth: true, role: 'ROLE_ADMIN' },
  },
  {
    path: '/admin/users',
    name: 'AdminUsers',
    component: AdminUsers,
    meta: { requiresAuth: true, role: 'ROLE_ADMIN' },
  },
  {
    path: '/admin/products',
    name: 'AdminProducts',
    component: AdminProducts,
    meta: { requiresAuth: true, role: 'ROLE_ADMIN' },
  },
  // User routes
  {
    path: '/user/dashboard',
    name: 'UserDashboard',
    component: UserDashboard,
    meta: { requiresAuth: true, role: 'ROLE_USER' },
  },
  {
    path: '/user/profile',
    name: 'UserProfile',
    component: UserProfile,
    meta: { requiresAuth: true, role: 'ROLE_USER' },
  },
  {
    path: '/user/products',
    name: 'ProductList',
    component: ProductList,
    meta: { requiresAuth: true, role: 'ROLE_USER' },
  },
  {
    path: '/user/cart',
    name: 'UserCart',
    component: UserCart,
    meta: { requiresAuth: true, role: 'ROLE_USER' },
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

// List of public pages (no authentication required)
const publicPages = ['/', '/login', '/register'];

router.beforeEach((to, from, next) => {
  const user = localStorage.getItem('user')
    ? JSON.parse(localStorage.getItem('user'))
    : null;

  const isPublic = publicPages.includes(to.path);
  const requiresAuth = to.meta.requiresAuth;
  const userRole = user?.role;

  // ✅ Allow access to public pages
  if (isPublic) {
    return next();
  }

  // ✅ If authentication is required but user is not logged in, redirect to login
  if (requiresAuth && !user) {
    return next('/login');
  }

  // ✅ Role-based access control: Ensure user has permission for the route
  if (to.meta.role && userRole !== to.meta.role) {
    console.warn(`Access denied: ${userRole} tried to access ${to.name}`);

    // Redirect users to their correct dashboard instead of looping
    return userRole === 'ROLE_ADMIN'
      ? next('/admin/dashboard')
      : userRole === 'ROLE_USER'
      ? next('/user/dashboard')
      : next('/login');
  }

  next();
});

export default router;
