import { createRouter, createWebHistory } from "vue-router";
import HomePage from "@/components/HomePage.vue";
import LoginPage from "@/components/LoginPage.vue";
import RegisterPage from "@/components/RegisterPage.vue";
// Admin pages
import AdminDashboard from "@/components/admin/AdminDashboard.vue";
import AdminProfile from "@/components/admin/AdminProfile.vue";
import AdminUsers from "@/components/admin/AdminUsers.vue";
import AdminProducts from "@/components/admin/AdminProducts.vue";
import EditAdminProfile from "@/components/admin/EditAdminProfile.vue"; // Ensure this component exists
// User pages
import UserDashboard from "@/components/user/UserDashboard.vue";
import UserProfile from "@/components/user/UserProfile.vue";
import ProductList from "@/components/user/ProductList.vue";
import UserCart from "@/components/user/UserCart.vue";
import AuthService from "@/services/AuthService";

const routes = [
  { path: "/", name: "Home", component: HomePage },
  { path: "/login", name: "Login", component: LoginPage },
  { path: "/register", name: "Register", component: RegisterPage },
  // Admin routes
  {
    path: "/admin/dashboard",
    name: "AdminDashboard",
    component: AdminDashboard,
    meta: { requiresAuth: true, role: "ADMIN" },
  },
  {
    path: "/admin/profile",
    name: "AdminProfile",
    component: AdminProfile,
    meta: { requiresAuth: true, role: "ADMIN" },
  },
  {
    path: "/admin/users",
    name: "AdminUsers",
    component: AdminUsers,
    meta: { requiresAuth: true, role: "ADMIN" },
  },
  {
    path: "/admin/products",
    name: "AdminProducts",
    component: AdminProducts,
    meta: { requiresAuth: true, role: "ADMIN" },
  },
  {
    path: "/admin/edit-profile",
    name: "EditAdminProfile",
    component: EditAdminProfile,
    meta: { requiresAuth: true, role: "ADMIN" },
  },
  // User routes
  {
    path: "/user/dashboard",
    name: "UserDashboard",
    component: UserDashboard,
    meta: { requiresAuth: true, role: "ROLE_USER" },
  },
  {
    path: "/user/profile",
    name: "UserProfile",
    component: UserProfile,
    meta: { requiresAuth: true, role: "ROLE_USER" },
  },
  {
    path: "/user/products",
    name: "ProductList",
    component: ProductList,
    meta: { requiresAuth: true, role: "ROLE_USER" },
  },
  {
    path: "/user/cart",
    name: "UserCart",
    component: UserCart,
    meta: { requiresAuth: true, role: "ROLE_USER" },
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

// List of public pages (no authentication required)
router.beforeEach((to, from, next) => {
  const requiresAuth = to.matched.some((record) => record.meta.requiresAuth);
  const user = AuthService.getUser();
  const role = user ? user.role : null;

  if (requiresAuth && !user) {
    next({ name: "Login" });
  } else if (requiresAuth && to.meta.role && to.meta.role !== role) {
    next({ name: "Home" });
  } else {
    next();
  }
});

export default router;
